CREATE DATABASE tbl_usertbl_user;CREATE TABLE `tbl_user` (  `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,  `nohp` VARCHAR(191) COLLATE utf8mb4_unicode_ci NOT NULL,  `email` VARCHAR(191) COLLATE utf8mb4_unicode_ci NOT NULL,  `alamat` TEXT COLLATE utf8mb4_unicode_ci NOT NULL,  `password` VARCHAR(191) COLLATE utf8mb4_unicode_ci NOT NULL,  `remember_token` VARCHAR(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,  `created_at` TIMESTAMP NULL DEFAULT NULL,  `updated_at` TIMESTAMP NULL DEFAULT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `tbl_user_email_unique` (`email`));CREATE TABLE `tbl_produk` (  `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,  `nama_produk` VARCHAR(191) COLLATE utf8mb4_unicode_ci NOT NULL,  `image` VARCHAR(191) COLLATE utf8mb4_unicode_ci NOT NULL,  `harga` INTEGER(20) COLLATE utf8mb4_unicode_ci NOT NULL,  `stok` INTEGER(20) COLLATE utf8mb4_unicode_ci NOT NULL,  PRIMARY KEY (`id`));